import React from "react";
import { useParams, Link } from "react-router-dom";
import { alumniData } from "./data";

const AlumniDetail = () => {
  const { id } = useParams();
  const alumni = alumniData.find((alumnus) => alumnus.id === parseInt(id));

  if (!alumni) {
    return <h2 className="text-center text-red-500">Alumni not found</h2>;
  }

  return (
    <div className="container mx-auto p-6">
      <div className="bg-white shadow-lg rounded-lg p-6 text-center">
        <img src={alumni.image} alt={alumni.name} className="w-40 h-40 object-cover rounded-full mx-auto" />
        <h1 className="text-2xl font-bold mt-4">{alumni.name}</h1>
        <p className="text-gray-600">{alumni.role}</p>
        <p className="text-sm text-gray-500">Class of {alumni.classYear}</p>
        <a href={alumni.linkedin} target="_blank" rel="noopener noreferrer" className="text-blue-500">
          LinkedIn Profile
        </a>

        <h2 className="text-xl font-semibold mt-6">Projects</h2>
        <ul className="list-disc mt-2 text-left mx-auto w-2/3">
          {alumni.projects.map((project, index) => (
            <li key={index} className="text-gray-700">{project}</li>
          ))}
        </ul>

        <Link to="/" className="mt-4 inline-block bg-blue-500 text-white px-4 py-2 rounded">
          Back to Alumni List
        </Link>
      </div>
    </div>
  );
};

export default AlumniDetail;
