import React, { useState } from 'react';

const alumniData = {
  'Data Science': [
    { name: 'B. Dhanush', project: 'Netflix User Behavior Prediction' },
    { name: 'M. Koushik Manohar', project: 'Sales Forecasting Model' },
  ],
  'AI / ML': [
    { name: 'Riya Kapoor', project: 'Chatbot for Campus Queries' },
    { name: 'Ankit Rao', project: 'AI Resume Scanner' },
  ],
  // Add more streams as needed
};

const StudentPage = () => {
  const [form, setForm] = useState({ name: '', stream: '', project: '' });
  const [results, setResults] = useState([]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (alumniData[form.stream]) {
      setResults(alumniData[form.stream]);
    } else {
      setResults([]);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-white to-purple-100 p-6">
      <h1 className="text-4xl font-bold text-center text-purple-800 mb-6">Student Search</h1>

      <form
        onSubmit={handleSubmit}
        className="max-w-xl mx-auto bg-white p-6 rounded-2xl shadow-lg border border-purple-200"
      >
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={form.name}
          onChange={handleChange}
          required
          className="w-full p-2 mb-4 border rounded"
        />
        <select
          name="stream"
          value={form.stream}
          onChange={handleChange}
          required
          className="w-full p-2 mb-4 border rounded"
        >
          <option value="">Select Stream</option>
          <option value="Data Science">Data Science</option>
          <option value="AI / ML">AI / ML</option>
          <option value="Cybersecurity">Cybersecurity</option>
          <option value="BBA">BBA</option>
        </select>
        <input
          type="text"
          name="project"
          placeholder="Your Current Project"
          value={form.project}
          onChange={handleChange}
          required
          className="w-full p-2 mb-4 border rounded"
        />
        <button
          type="submit"
          className="bg-purple-600 text-white px-6 py-2 rounded hover:bg-purple-700"
        >
          Search Alumni
        </button>
      </form>

      {results.length > 0 && (
        <div className="mt-10 max-w-3xl mx-auto">
          <h2 className="text-2xl font-semibold text-purple-700 mb-4">Alumni from {form.stream}</h2>
          <ul className="space-y-4">
            {results.map((alum, index) => (
              <li key={index} className="bg-white p-4 rounded shadow border-l-4 border-purple-400">
                <strong>{alum.name}</strong>: {alum.project}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default StudentPage;
