import React from "react";
import { useNavigate, Link } from "react-router-dom"; // ✅ Added Link here
import "./styles.css";

import dsImage from "./assets/What-is-data-science-2.jpg";
import aimlImage from "./assets/aiml-image-1.webp";
import cyberImage from "./assets/cyber-image.png";
import bbaImage from "./assets/images-bba.jpg";

const specializations = [
    { name: "Data Science", image: dsImage, path: "/alumni/datascience" },
    { name: "AI / ML", image: aimlImage, path: "/alumni/aiml" },
    { name: "Cybersecurity", image: cyberImage, path: "/alumni/cyber" },
    { name: "BBA", image: bbaImage, path: "/alumni/bba" },
];

const HomePage = () => {
    const navigate = useNavigate();

    return (
        <div className="homepage relative"> {/* ✅ Make this relative for absolute positioning inside */}
            {/* Student Icon Link in Top Right */}
            <Link
                to="/student"
                className="absolute top-4 right-6 bg-blue-600 text-white px-4 py-2 rounded-full hover:bg-blue-700 transition"
            >
                Student
            </Link>

            {/* TITLE TILE */}
            <section className="hero-title-tile">
                <h1 className="hero-title">Alumni Edge</h1>
                <p className="hero-slogan">Learn from the Best.</p>
            </section>

            {/* ABOUT SECTION */}
            <section className="hero">
                <p>
                    Our Alumni Directory helps students connect with graduates from our college.
                    Whether you're working on projects, looking for mentorship, or just curious,
                    this platform lists alumni by specialization — from Data Science to BBA —
                    so you can find the right experts who walked the same halls before you.
                </p>
            </section>

            {/* SPECIALIZATIONS */}
            <section className="specializations">
                <h2>Explore Specializations</h2>
                <div className="specialization-grid">
                    {specializations.map((spec, index) => (
                        <div key={index} className="spec-card" onClick={() => navigate(spec.path)}>
                            <img src={spec.image} alt={spec.name} />
                            <h3>{spec.name}</h3>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};

export default HomePage;
