import React from "react";
import { Button } from "@/components/ui/button";
import { Link } from "react-router-dom";
import heroImage from "./assets/diverse-group-students-interacting-21860865.webp";
import aboutImage from "./assets/introduction.png";

const HomePage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 via-white to-blue-100 p-6">
      {/* Title Tile Section */}
      <div
        className="rounded-3xl shadow-xl overflow-hidden mb-10 bg-cover bg-center"
        style={{
          backgroundImage: `url(${heroImage})`,
          height: "400px",
        }}
      >
        <div className="bg-black bg-opacity-60 h-full flex items-center justify-center px-10">
          <div className="text-center text-white space-y-4">
            <h1 className="text-5xl font-bold drop-shadow-lg">Alumni Edge</h1>
            <p className="text-lg drop-shadow-md">
              Bridging Generations of Students & Alumni
            </p>
            <div className="flex justify-center gap-4 pt-4">
              <Link to="/student">
                <Button className="rounded-2xl shadow-md px-6 py-2 text-lg">Student</Button>
              </Link>
              <Link to="/alumni">
                <Button className="rounded-2xl shadow-md px-6 py-2 text-lg" variant="secondary">
                  Alumni
                </Button>
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* About Section */}
      <div className="bg-white rounded-3xl shadow-lg p-10 grid md:grid-cols-2 gap-8 items-center">
        <img
          src={aboutImage}
          alt="About"
          className="rounded-2xl shadow-md object-cover w-full h-80"
        />
        <div>
          <h2 className="text-3xl font-semibold mb-4 text-blue-800">About This Project</h2>
          <p className="text-gray-700 text-lg leading-relaxed">
            This Alumni Directory connects students with graduates based on their specialization.
            Students can explore alumni profiles, learn about their career paths and projects, and
            gain valuable inspiration. Alumni can showcase their achievements and share their journey
            with the next generation.
          </p>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
