import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./styles.css";

import dsImage from "./assets/What-is-data-science-2.jpg";
import aimlImage from "./assets/aiml-image-1.webp";
import cyberImage from "./assets/cyber-image.png";
import bbaImage from "./assets/images-bba.jpg";

const specializations = [
  { name: "Data Science", image: dsImage, path: "/alumni/datascience" },
  { name: "AI / ML", image: aimlImage, path: "/alumni/aiml" },
  { name: "Cybersecurity", image: cyberImage, path: "/alumni/cyber" },
  { name: "BBA", image: bbaImage, path: "/alumni/bba" },
];

const HomePage = () => {
  const navigate = useNavigate();
  const [animateTitle, setAnimateTitle] = useState(false);

  useEffect(() => {
    setAnimateTitle(true);
  }, []);

  return (
    <div className="homepage bg-gray-50 min-h-screen">
      {/* ✅ NAVBAR */}
      <div className="navbar fixed top-0 w-full bg-white shadow-md z-50 flex justify-between items-center px-10 py-4">
        <div className="text-2xl font-bold text-blue-700">Alumni Edge</div>
        <div className="flex gap-4">
          <button
            onClick={() => navigate("/alumni")}
            className="bg-blue-600 text-white px-5 py-2 rounded-full hover:bg-blue-700 transition"
          >
            Alumni
          </button>
          <button
            onClick={() => navigate("/student")}
            className="bg-blue-600 text-white px-5 py-2 rounded-full hover:bg-blue-700 transition"
          >
            Student
          </button>
        </div>
      </div>

      {/* Spacer to prevent content hiding behind fixed navbar */}
      <div className="h-24" />

      {/* ✅ HERO TITLE TILE */}
      <section
        className={`max-w-5xl mx-auto bg-white rounded-3xl shadow-xl px-10 py-16 text-center transition-all duration-700 ease-out ${
          animateTitle ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
        }`}
      >
        <h1 className="text-5xl font-extrabold text-blue-700 mb-4">
          Alumni Edge
        </h1>
        <p className="text-xl text-gray-600 mb-8">Learn from the Best.</p>
        <div className="flex justify-center gap-6">
          <button
            onClick={() => navigate("/alumni")}
            className="bg-blue-600 text-white px-6 py-3 rounded-full text-lg hover:bg-blue-700 transition"
          >
            Alumni
          </button>
          <button
            onClick={() => navigate("/student")}
            className="bg-blue-600 text-white px-6 py-3 rounded-full text-lg hover:bg-blue-700 transition"
          >
            Student
          </button>
        </div>
      </section>

      {/* ✅ ABOUT SECTION */}
      <section className="max-w-4xl mx-auto mt-16 text-center px-6">
        <p className="text-lg text-gray-700">
          Our Alumni Directory helps students connect with graduates from our
          college. Whether you're working on projects, looking for mentorship,
          or just curious, this platform lists alumni by specialization — from
          Data Science to BBA — so you can find the right experts who walked the
          same halls before you.
        </p>
      </section>

      {/* ✅ SPECIALIZATIONS */}
      <section className="mt-20 px-6">
        <h2 className="text-3xl font-bold text-center text-blue-800 mb-10">
          Explore Specializations
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-6xl mx-auto">
          {specializations.map((spec, index) => (
            <div
              key={index}
              className="spec-card bg-white shadow-md rounded-2xl overflow-hidden hover:shadow-xl transition-transform transform hover:scale-105 cursor-pointer"
              onClick={() => navigate(spec.path)}
            >
              <img
                src={spec.image}
                alt={spec.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4 text-center">
                <h3 className="text-xl font-semibold text-gray-800">
                  {spec.name}
                </h3>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
