import React from 'react';
import img1 from '../assets/image-bba1.jpg';
import img2 from '../assets/image-bba2.jpg';
import img3 from '../assets/image-bba3.jpg';

const BbaAlumni = () => {
  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h1 className="text-3xl sm:text-4xl font-bold text-center mb-6">BBA Alumni</h1>
      <p className="text-center text-gray-600 mb-10 max-w-3xl mx-auto text-base sm:text-lg">
        Explore the success stories of our BBA graduates who have become leaders in business, management, and entrepreneurship, shaping industries around the globe.
      </p>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Alumni 1 */}
        <div className="bg-white shadow-lg rounded-2xl p-6 transform hover:-translate-y-2 transition duration-300 ease-in-out hover:shadow-2xl">
          <img src={img1} alt="Aarav Malhotra" className="w-full h-32 object-cover rounded-xl mb-4" />
          <h2 className="text-lg font-semibold">Aarav Malhotra</h2>
          <p className="text-sm text-gray-500">CEO, FinEdge Corp</p>
          <p className="text-sm mt-2">Class of 2010</p>
        </div>

        {/* Alumni 2 */}
        <div className="bg-white shadow-lg rounded-2xl p-6 transform hover:-translate-y-2 transition duration-300 ease-in-out hover:shadow-2xl">
          <img src={img2} alt="Simran Kaur" className="w-full h-32 object-cover rounded-xl mb-4" />
          <h2 className="text-lg font-semibold">Simran Kaur</h2>
          <p className="text-sm text-gray-500">Marketing Director, Unilever</p>
          <p className="text-sm mt-2">Class of 2013</p>
        </div>

        {/* Alumni 3 */}
        <div className="bg-white shadow-lg rounded-2xl p-6 transform hover:-translate-y-2 transition duration-300 ease-in-out hover:shadow-2xl">
          <img src={img3} alt="Rajat Gupta" className="w-full h-32 object-cover rounded-xl mb-4" />
          <h2 className="text-lg font-semibold">Rajat Gupta</h2>
          <p className="text-sm text-gray-500">Co-Founder, StartX India</p>
          <p className="text-sm mt-2">Class of 2016</p>
        </div>
      </div>
    </div>
  );
};

export default BbaAlumni;
